mapper.select:
SELECT DISTINCT A.id AS id, A.user_account AS userAccount, A.user_nick_name AS userNickName, A.user_name AS userName, A.user_sex AS userSex, A.user_email AS userEmail, A.user_mobile AS userMobile, A.user_password AS userPassword, A.user_status AS userStatus, A.head_img_url AS headImgUrl, A.province AS province, D.name AS provinceName, A.city AS city, E.name AS cityName, A.area AS area, A.street AS street, A.description AS description, A.create_time AS createTime, A.creator AS creator, A.update_time AS updateTime, A.OPERATOR AS operator, GROUP_CONCAT(DISTINCT C.id) AS roleIds, GROUP_CONCAT(DISTINCT C.role_key) AS roleKey, GROUP_CONCAT(DISTINCT C.role_name) AS roleName, GROUP_CONCAT(DISTINCT H.organization_id) AS dataPermission, G.id AS organizationId, G.organization_name AS organizationName FROM t_sys_user A INNER JOIN t_sys_user_role B ON A.id = B.user_id AND B.del_flag = 0 LEFT JOIN t_sys_role C ON B.role_id = C.id AND C.del_flag = 0 LEFT JOIN t_sys_district D ON A.province = D.code LEFT JOIN t_sys_district E ON A.city = E.code LEFT JOIN t_sys_data_permission H ON A.id = H.user_id AND H.del_flag = 0 INNER JOIN t_sys_organization_user F ON A.id = F.user_id AND F.del_flag = 0 INNER JOIN t_sys_organization G ON F.organization_id = G.id AND G.del_flag = 0 WHERE A.del_flag = 0
AND A.id = #{user.id}
AND A.user_account like concat('%', #{user.userAccount}, '%')
AND A.user_nick_name like concat('%', #{user.userNickName}, '%')
AND A.user_name like concat('%', #{user.userName}, '%')
AND A.user_mobile like concat('%', #{user.userMobile}, '%')
AND A.user_email like concat('%', #{user.userEmail}, '%')
AND A.user_status = #{user.userStatus}
AND B.role_id = #{user.roleId}
AND G.id = #{user.organizationId}
AND DATE_FORMAT(A.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{user.startDate},'%Y-%m-%d')
AND DATE_FORMAT(A.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{user.endDate},'%Y-%m-%d')
GROUP BY A.id ORDER BY createTime DESC
